import React, { useState } from "react";
import { createPerson } from "../api/people";

function EmployeeForm({ onAddEmployee }) {
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      // Read existing form fields
      const fd = new FormData(e.currentTarget);

      const payload = {
        name: fd.get("name") || fd.get("full_name") || "",
        title: fd.get("title") || fd.get("position") || "",
        department: fd.get("department") || "",
        status: fd.get("status") || "Employee",
        startDate: fd.get("startDate") || fd.get("start_date") || "",
        location: fd.get("location") || fd.get("timezone") || "",
        acdc_email: fd.get("acdc_email") || fd.get("acdcEmail") || "",
        personal_email: fd.get("personal_email") || fd.get("personalEmail") || "",
        phone: fd.get("phone") || fd.get("phone_number") || "",
        subteam: fd.get("subteam") || "",
      };

      // Send to backend
      const created = await createPerson(payload);

      // Map backend response to frontend
      const mapped = {
        name: created.full_name,
        title: created.position,
        department: created.department,
        status:
          created.status === "on_leave"
            ? "On leave"
            : created.status === "active"
            ? "Employee"
            : "Inactive",
        startDate: created.start_date,
        location: created.timezone,
        acdc_email: created.acdc_email,
        personal_email: created.personal_email,
        phone: created.phone,
      };

      onAddEmployee(mapped);
      e.currentTarget.reset();
      setMessage("✅ Employee added successfully");
    } catch (err) {
      console.error("Error creating employee:", err);
      setMessage("❌ Failed to add employee");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="employee-form-container">
      <h2>Add New Employee</h2>
      <form onSubmit={handleSubmit} className="employee-form">
        {/* Keep all original styling/layout intact */}
        <input name="name" placeholder="Full Name" required />
        <input name="title" placeholder="Position / Title" />
        <input name="department" placeholder="Department" required />
        <input name="status" placeholder="Status (Employee, On leave...)" defaultValue="Employee" />
        <input type="date" name="startDate" />
        <input name="location" placeholder="Timezone / Location" />
        <input name="acdc_email" placeholder="ACDC Email" />
        <input name="personal_email" placeholder="Personal Email" />
        <input name="phone" placeholder="Phone Number" />

        <button type="submit" disabled={loading}>
          {loading ? "Adding..." : "Add Employee"}
        </button>
      </form>

      {message && <p>{message}</p>}
    </div>
  );
}

export default EmployeeForm;
