// src/components/EmployeeForm.js
import React, { useState } from 'react';

const defaultForm = {
  full_name: '',
  position: '',
  department: '',
  status: 'Employee',
  start_date: '',
  timezone: '',
  acdc_email: '',
  personal_email: '',
  phone: '',
};

export default function EmployeeForm({ onAddEmployee }) {
  const [form, setForm] = useState(defaultForm);
  const [submitting, setSubmitting] = useState(false);

  function handleChange(e) {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  }

  async function handleSubmit(e) {
    e.preventDefault();
    if (submitting) return;
    setSubmitting(true);

    try {
      // Pass the raw object up – your parent handles POST for now
      onAddEmployee?.(form);
      setForm(defaultForm);
    } finally {
      setSubmitting(false);
    }
  }

  return (
    <section className="employee-form-section" id="add-employee">
      <h2 className="employee-form-title">Add New Employee</h2>

      <form className="employee-form" onSubmit={handleSubmit}>
        <div className="form-grid">
          {/* Full name */}
          <div className="form-field">
            <label htmlFor="full_name">Full Name</label>
            <input
              id="full_name"
              name="full_name"
              type="text"
              placeholder="Full Name"
              value={form.full_name}
              onChange={handleChange}
              required
            />
          </div>

          {/* Position / Title */}
          <div className="form-field">
            <label htmlFor="position">Position / Title</label>
            <input
              id="position"
              name="position"
              type="text"
              placeholder="Position / Title"
              value={form.position}
              onChange={handleChange}
            />
          </div>

          {/* Department */}
          <div className="form-field">
            <label htmlFor="department">Department</label>
            <input
              id="department"
              name="department"
              type="text"
              placeholder="Department"
              value={form.department}
              onChange={handleChange}
            />
          </div>

          {/* Status */}
          <div className="form-field">
            <label htmlFor="status">Status</label>
            <select
              id="status"
              name="status"
              value={form.status}
              onChange={handleChange}
            >
              <option value="Employee">Employee</option>
              <option value="Volunteer">Volunteer</option>
              <option value="Director">Director</option>
              <option value="Admin">Admin</option>
              <option value="on_leave">On Leave</option>
              <option value="inactive">Inactive</option>
            </select>
          </div>

          {/* Start Date */}
          <div className="form-field">
            <label htmlFor="start_date">Start Date</label>
            <input
              id="start_date"
              name="start_date"
              type="date"
              value={form.start_date}
              onChange={handleChange}
            />
          </div>

          {/* Timezone / Location */}
          <div className="form-field">
            <label htmlFor="timezone">Timezone / Location</label>
            <input
              id="timezone"
              name="timezone"
              type="text"
              placeholder="Timezone / Location"
              value={form.timezone}
              onChange={handleChange}
            />
          </div>

          {/* ACDC Email */}
          <div className="form-field">
            <label htmlFor="acdc_email">ACDC Email</label>
            <input
              id="acdc_email"
              name="acdc_email"
              type="email"
              placeholder="ACDC Email"
              value={form.acdc_email}
              onChange={handleChange}
            />
          </div>

          {/* Personal Email */}
          <div className="form-field">
            <label htmlFor="personal_email">Personal Email</label>
            <input
              id="personal_email"
              name="personal_email"
              type="email"
              placeholder="Personal Email"
              value={form.personal_email}
              onChange={handleChange}
            />
          </div>

          {/* Phone */}
          <div className="form-field">
            <label htmlFor="phone">Phone Number</label>
            <input
              id="phone"
              name="phone"
              type="tel"
              placeholder="Phone Number"
              value={form.phone}
              onChange={handleChange}
            />
          </div>
        </div>

        <div className="form-actions">
          <button type="submit" disabled={submitting}>
            {submitting ? 'Adding…' : 'Add Employee'}
          </button>
        </div>
      </form>
    </section>
  );
}
