import React, { useState } from "react";
import { createPerson } from "../api/people";

function EmployeeForm({ onAddEmployee }) {
  const [formData, setFormData] = useState({
    name: "",
    title: "",
    department: "",
    status: "Employee",
    startDate: "",
    location: "",
    acdc_email: "",
    personal_email: "",
    phone: "",
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      // Send to backend
      const newPerson = await createPerson(formData);
      setMessage("Employee added successfully ✅");

      // Reflect instantly in frontend
      onAddEmployee({
        name: newPerson.full_name,
        title: newPerson.position,
        department: newPerson.department,
        status:
          newPerson.status === "on_leave"
            ? "On leave"
            : newPerson.status === "active"
            ? "Employee"
            : "Inactive",
        startDate: newPerson.start_date,
        location: newPerson.timezone,
        acdc_email: newPerson.acdc_email,
        personal_email: newPerson.personal_email,
        phone: newPerson.phone,
      });

      // Reset form
      setFormData({
        name: "",
        title: "",
        department: "",
        status: "Employee",
        startDate: "",
        location: "",
        acdc_email: "",
        personal_email: "",
        phone: "",
      });
    } catch (err) {
      console.error("Error creating employee:", err);
      setMessage("❌ Failed to add employee");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="employee-form">
      <h2>Add New Employee</h2>
      <form onSubmit={handleSubmit}>
        <input
          name="name"
          placeholder="Full Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          name="title"
          placeholder="Position / Title"
          value={formData.title}
          onChange={handleChange}
        />
        <input
          name="department"
          placeholder="Department"
          value={formData.department}
          onChange={handleChange}
          required
        />
        <input
          name="status"
          placeholder="Status"
          value={formData.status}
          onChange={handleChange}
        />
        <input
          type="date"
          name="startDate"
          value={formData.startDate}
          onChange={handleChange}
        />
        <input
          name="location"
          placeholder="Timezone / Location"
          value={formData.location}
          onChange={handleChange}
        />
        <input
          name="acdc_email"
          placeholder="ACDC Email"
          value={formData.acdc_email}
          onChange={handleChange}
        />
        <input
          name="personal_email"
          placeholder="Personal Email"
          value={formData.personal_email}
          onChange={handleChange}
        />
        <input
          name="phone"
          placeholder="Phone Number"
          value={formData.phone}
          onChange={handleChange}
        />

        <button type="submit" disabled={loading}>
          {loading ? "Adding..." : "Add Employee"}
        </button>
      </form>

      {message && <p>{message}</p>}
    </div>
  );
}

export default EmployeeForm;
