import React, { useState } from 'react';
import Navigation from './components/Navigation';
import Hero from './components/Hero';
import EmployeeDirectory from './components/EmployeeDirectory';
import EmployeeForm from './components/EmployeeForm';
import Contact from './components/Contact';
import Footer from './components/Footer';
import './App.css';

function App() {
  const [employees, setEmployees] = useState([
    {name: 'Brooke Khan', location: 'New York City', title: 'VP of Sales', status: 'On leave', startDate: '2015-01-13', department: 'Sales', reportsTo: 'Pat Everett'},
    {name: 'Sandy Hagen', location: 'New York City', title: 'Product Designer', status: 'Employee', startDate: '2018-02-01', department: 'Design', reportsTo: 'Jamie Ziya'},
    {name: 'Jamie Ziya', location: 'New York City', title: 'VP of Product', status: 'Employee', startDate: '2013-05-30', department: 'Product Management', reportsTo: 'Pat Everett'},
    {name: 'Pat Everett', location: 'New York City', title: 'CEO', status: 'Employee', startDate: '2014-08-24', department: 'Executive', reportsTo: ''},
    {name: 'Kai Siyavong', location: 'New York City', title: 'PMM', status: 'Employee', startDate: '2017-02-15', department: 'Product Marketing', reportsTo: 'Jamie Ziya'},
    {name: 'Robby Pritchett', location: 'San Francisco', title: 'VP of Engineering', status: 'Employee', startDate: '2016-01-23', department: 'Engineering', reportsTo: 'Pat Everett'},
    {name: 'Dany Coronado', location: 'San Francisco', title: 'Software Engineer', status: 'Employee', startDate: '2010-01-11', department: 'Engineering', reportsTo: 'Robby Pritchett'},
    {name: 'River Oe', location: 'San Francisco', title: 'Product Manager', status: 'Employee', startDate: '2011-12-30', department: 'Product Management', reportsTo: 'Jamie Ziya'}
  ]);

  const addEmployee = (newEmployee) => {
    setEmployees([...employees, newEmployee]);
  };

  return (
    <div className="App">
      <Navigation />
      <Hero />
      <EmployeeDirectory employees={employees} />
      <EmployeeForm onAddEmployee={addEmployee} />
      <Contact />
      <Footer />
    </div>
  );
}

export default App;
